INSERT INTO `news` VALUES (15, '2', NULL, 'http://localhost:8089/images/cba4769a-7701-4b88-8707-954a63beaa0e.png', '暑假放假公告', '<p style=\"text-align: start;\">1、月圆，团圆，事事圆。</p><p style=\"text-align: start;\">2、花好人更好，月圆人团圆。</p><p style=\"text-align: start;\">3、中秋合家欢。</p><p style=\"text-align: start;\">4、传统风采，回味无穷。</p><p style=\"text-align: start;\">5、月色柔和的凉爽中秋。</p><p style=\"text-align: start;\">6、十五团圆合家欢，中秋月饼话神仙。</p><p style=\"text-align: start;\">7、每逢佳节倍思亲，中秋之月大团圆。</p><p style=\"text-align: start;\">8、月是故乡明，月饼表浓情。</p><p style=\"text-align: start;\">9、月圆天下，品尝美味。</p><p style=\"text-align: start;\"><img src=\"https://pic4.zhimg.com/80/v2-b7daf8272e5a983f7ff3b3312eeb5c93_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">10、月是故乡明，情是亲人浓。</p><p style=\"text-align: start;\">11、花好月圆的世界，中秋情浓意更浓。</p><p style=\"text-align: start;\">12、遥寄相思中秋梦，千里故人何处逢。</p><p style=\"text-align: start;\">13、天涯共此时，千里共婵娟。</p><p style=\"text-align: start;\">14、何以团圆，唯有中秋</p><p style=\"text-align: start;\">15、桂花飘香传玉宇，明月清辉人间驻</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">中秋习俗</p><p style=\"text-align: start;\">吃月饼</p><p style=\"text-align: start;\">赏月</p><p style=\"text-align: start;\">DIY月饼</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">主题❶话说中秋</p><p style=\"text-align: start;\">▪️中秋节的由来</p><p style=\"text-align: start;\">▪️中秋节的传说与民间故事</p><p style=\"text-align: start;\">▪️中秋诗词佳句知多少</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">主题❷中秋实践活动</p><p style=\"text-align: start;\">▪️设计别致、精美的月饼图案</p><p style=\"text-align: start;\">▪️举办月饼宴</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">❸知识竞赛</p><p style=\"text-align: start;\">1、八月十五又称什么节？</p><p style=\"text-align: start;\">　　a、月饼节</p><p style=\"text-align: start;\">　　b、团圆节</p><p style=\"text-align: start;\">　　c、故乡节</p><p style=\"text-align: start;\">　　d、诗人节</p><p style=\"text-align: start;\">2、在哪个朝代中秋节才开始成为固定的节日？</p><p style=\"text-align: start;\">　　a、唐</p><p style=\"text-align: start;\">　　b、宋</p><p style=\"text-align: start;\">　　c、元</p><p style=\"text-align: start;\">　　d、明</p><p style=\"text-align: start;\"><img src=\"https://pic1.zhimg.com/80/v2-414e6d754832ed401cd9e3a928234980_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">3、以下哪个不是中秋节传说？</p><p style=\"text-align: start;\">　　a、嫦娥奔月</p><p style=\"text-align: start;\">　　b、吴刚伐桂</p><p style=\"text-align: start;\">　　c、玉兔捣药</p><p style=\"text-align: start;\">　　d、迎涛神说</p><p style=\"text-align: start;\">4、在古代月圆和月缺一般形容什么？</p><p style=\"text-align: start;\">　　a、悲欢离合</p><p style=\"text-align: start;\">　　b、天气的好坏</p><p style=\"text-align: start;\">　　c、凶吉的象征</p><p style=\"text-align: start;\">　　d、身体是否健康</p><p style=\"text-align: start;\">5、月饼最初是用来做什么的？</p><p style=\"text-align: start;\">　　a、祭奉月神的祭品</p><p style=\"text-align: start;\">　　b、馈赠亲友的礼物</p><p style=\"text-align: start;\">　　c、节日食品</p><p style=\"text-align: start;\">　　d、地方小吃</p><p style=\"text-align: start;\">6、元宵节和端午节与下面哪个节日并称为我国三大传统佳节？</p><p style=\"text-align: start;\">　　a、春节</p><p style=\"text-align: start;\">　　b、重阳节</p><p style=\"text-align: start;\">　　c、中秋节</p><p style=\"text-align: start;\">　　d、圣诞节</p>', '2023-05-19 08:00:00', '1', '', '0');
INSERT INTO `news` VALUES (16, '2', NULL, 'http://localhost:8089/images/74b949aa-eb10-46c4-9e7a-4d7bd1da7e83.jpeg', '社团合并公告', '<p style=\"text-align: start;\">1、月圆，团圆，事事圆。</p><p style=\"text-align: start;\">2、花好人更好，月圆人团圆。</p><p style=\"text-align: start;\">3、中秋合家欢。</p><p style=\"text-align: start;\">4、传统风采，回味无穷。</p><p style=\"text-align: start;\">5、月色柔和的凉爽中秋。</p><p style=\"text-align: start;\">6、十五团圆合家欢，中秋月饼话神仙。</p><p style=\"text-align: start;\">7、每逢佳节倍思亲，中秋之月大团圆。</p><p style=\"text-align: start;\">8、月是故乡明，月饼表浓情。</p><p style=\"text-align: start;\">9、月圆天下，品尝美味。</p><p style=\"text-align: start;\"><img src=\"https://pic4.zhimg.com/80/v2-b7daf8272e5a983f7ff3b3312eeb5c93_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">10、月是故乡明，情是亲人浓。</p><p style=\"text-align: start;\">11、花好月圆的世界，中秋情浓意更浓。</p><p style=\"text-align: start;\">12、遥寄相思中秋梦，千里故人何处逢。</p><p style=\"text-align: start;\">13、天涯共此时，千里共婵娟。</p><p style=\"text-align: start;\">14、何以团圆，唯有中秋</p><p style=\"text-align: start;\">15、桂花飘香传玉宇，明月清辉人间驻</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">中秋习俗</p><p style=\"text-align: start;\">吃月饼</p><p style=\"text-align: start;\">赏月</p><p style=\"text-align: start;\">DIY月饼</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">主题❶话说中秋</p><p style=\"text-align: start;\">▪️中秋节的由来</p><p style=\"text-align: start;\">▪️中秋节的传说与民间故事</p><p style=\"text-align: start;\">▪️中秋诗词佳句知多少</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">主题❷中秋实践活动</p><p style=\"text-align: start;\">▪️设计别致、精美的月饼图案</p><p style=\"text-align: start;\">▪️举办月饼宴</p><p style=\"text-align: start;\">·</p><p style=\"text-align: start;\">❸知识竞赛</p><p style=\"text-align: start;\">1、八月十五又称什么节？</p><p style=\"text-align: start;\">　　a、月饼节</p><p style=\"text-align: start;\">　　b、团圆节</p><p style=\"text-align: start;\">　　c、故乡节</p><p style=\"text-align: start;\">　　d、诗人节</p><p style=\"text-align: start;\">2、在哪个朝代中秋节才开始成为固定的节日？</p><p style=\"text-align: start;\">　　a、唐</p><p style=\"text-align: start;\">　　b、宋</p><p style=\"text-align: start;\">　　c、元</p><p style=\"text-align: start;\">　　d、明</p><p style=\"text-align: start;\"><img src=\"https://pic1.zhimg.com/80/v2-414e6d754832ed401cd9e3a928234980_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">3、以下哪个不是中秋节传说？</p><p style=\"text-align: start;\">　　a、嫦娥奔月</p><p style=\"text-align: start;\">　　b、吴刚伐桂</p><p style=\"text-align: start;\">　　c、玉兔捣药</p><p style=\"text-align: start;\">　　d、迎涛神说</p><p style=\"text-align: start;\">4、在古代月圆和月缺一般形容什么？</p><p style=\"text-align: start;\">　　a、悲欢离合</p><p style=\"text-align: start;\">　　b、天气的好坏</p><p style=\"text-align: start;\">　　c、凶吉的象征</p><p style=\"text-align: start;\">　　d、身体是否健康</p><p style=\"text-align: start;\">5、月饼最初是用来做什么的？</p><p style=\"text-align: start;\">　　a、祭奉月神的祭品</p><p style=\"text-align: start;\">　　b、馈赠亲友的礼物</p><p style=\"text-align: start;\">　　c、节日食品</p><p style=\"text-align: start;\">　　d、地方小吃</p><p style=\"text-align: start;\">6、元宵节和端午节与下面哪个节日并称为我国三大传统佳节？</p><p style=\"text-align: start;\">　　a、春节</p><p style=\"text-align: start;\">　　b、重阳节</p><p style=\"text-align: start;\">　　c、中秋节</p><p style=\"text-align: start;\">　　d、圣诞节</p>', '2023-05-19 08:00:00', '1', '', '0');
