-- ----------------------------
-- Table structure for team
-- ----------------------------
DROP TABLE IF EXISTS `team`;
CREATE TABLE `team`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `category_id` int(11) NULL DEFAULT NULL COMMENT '分类id',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '社团名称',
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '图片',
  `details` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL COMMENT '社团详情',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '创建时间',
  `has_top` varchar(2) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '0:未推荐 1：已推荐',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_bin ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of team
-- ----------------------------
INSERT INTO `team` VALUES (1, 2, '创业协会', 'http://localhost:8089/images/efbc4e7d-d66d-4192-a208-13393f2c029d.png', '<p style=\"text-indent: 32px; text-align: start;\">每一个上大学的学生，最终都会步入职场，因此在大学期间，如果能够一边学习专业也知识一边学习许多关于就业和创业方面的技能，绝对是一件大好事。<img src=\"https://img.wyfx2014.com/uploads/article/20220829/c6824e8b5db33131f5ef3a24706b8338.jpg\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-indent: 32px; text-align: start;\">因为在这个社团里，<strong>不但能够学习各种就业和创业的知识，还能够了解到各种就业渠道以及面试时候的技能和技巧，</strong>这些对于即将步入职场的大学生们来说，无疑是有很大帮助的，所以也被大学生们认为含金量很高的社团了。</p>', NULL, '1');
INSERT INTO `team` VALUES (2, 1, '篮球社团', 'http://localhost:8089/images/3ced0c48-4f88-4545-be56-3a0ab0f7529b.png', '<p><br></p><p style=\"text-indent: 32px; text-align: start;\">几乎每个大学里都会有文学社，在这个社团里，每位成员主要的工作就是研究与文学相关的知识和内容，平时还会涉及到一些文案的写作，因此<strong>不但能够培养学生们的文学气质，还能够提高大家的写作能力。</strong><img src=\"https://img.wyfx2014.com/uploads/article/20220829/2fef3670183e6d65859d8eca8e289379.jpg\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-indent: 32px; text-align: start;\">如果你拥有了很强的文案写作能力，在将来毕业找工作的时候，就会比别的竞争者更具有竞争力，<strong>如果你的能力特别出众，还可以考虑直接找一个类似文案编辑的工作，</strong>而你在社团里的一些业绩，也可以作为你踏入工作岗位的“工作经验”，因此这个社团绝对可以算得上是一个含金量高的社团了。</p><p><br></p>', '2023-04-29 08:00:00', '1');
INSERT INTO `team` VALUES (4, 3, '信息学设', 'http://localhost:8089/images/e6d4a302-e2ab-46fd-9a11-4115383e2877.png', '<p style=\"text-indent: 32px; text-align: start;\">在大学期间，无论你是学什么专业，都摆脱不了要学习英语的事实，所以如果能够加入一个可以帮助我们学习英语的社团，绝对是一个提高英语成绩的好机会。能够加入英语社团的学生，英语水平都不会太差，所以<strong>你平时也可以通过与社员的沟通和参加一些活动来提高自己的英语口语水平。</strong><img src=\"https://img.wyfx2014.com/uploads/article/20220829/2de43e27d562f1ad4c8b28f308a552fc.jpg\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-indent: 32px; text-align: start;\">而且英语社团里的一些成员早早的就通过了大学英语四级甚至六级的考试，<strong>加入这个社团还可以向他们多多请教学习英语的经验，让你在大学期间尽快通过英语四六级考试。</strong></p>', '2023-04-29 08:00:00', '1');
INSERT INTO `team` VALUES (5, 1, '自行车社团', 'http://localhost:8089/images/8ed1c3aa-e8d2-4bc3-b5ac-12254f7f5628.png', '<p style=\"text-indent: 32px; text-align: start;\">大学的辩论社里，不乏一些口才绝佳、思维敏捷的“辩论大佬”，他们经常能够代表自己的学校去参加各种辩论赛，并且能够在比赛中获得大奖。如果能够加入这样的社团的话，<strong>不但可以学习到很多关于辩论和沟通的技巧，还有机会参加全国大学生辩论赛，</strong>如果能够在这种全国性的辩论赛中获奖，对将来找工作也是有很大的帮助的，因此这也是一个含金量特别高的社团。<img src=\"https://img.wyfx2014.com/uploads/article/20220829/c5e76206b755dee383b27628ea5faf07.jpg\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-indent: 32px; text-align: start;\">以上就是今天总结的大学里含金量高的4个社团，如果在大学期间能够加入这样的社团，不但能够锻炼和提升自己能力，还会对以后找工作有好处，因此如果各位大一新生在大学开学以后，有幸收到了以上社团的邀请，请一定不要拒绝哦！</p>', '2023-05-03 08:00:00', '1');
INSERT INTO `team` VALUES (6, 2, '七草动漫社', 'http://localhost:8089/images/dbe3db68-5129-4caa-860b-ab23d9ed6240.png', '<p style=\"text-align: start;\"><strong>动漫社是指由热爱动画、漫画、游戏的团体组织而成的社团活动</strong>。</p><p style=\"text-align: start;\">以99度动漫社为例，99度动漫社成立于公元2000年，2018年，社团内部已成立宣传部、原创部、COS部、道具部、彩妆部、<a href=\"https://zhidao.baidu.com/search?word=%D5%AC%CE%E8&amp;fr=iknow_pc_qb_highlight\" target=\"\">宅舞</a>团、CV部、WOTA部、TG精英部共9个部门，形成了一套自给自足的一体化动漫社体系。</p><p style=\"text-align: start;\">社团涉及cosplay舞台剧表演编排、cosplay道具制作、cosplay舞台场景布置、cos场照及外景摄影、后期制作、原创绘画、原创视频制作、翻唱pv制作、宅舞表演、wota艺能演出等十余个领域。社团正努力从单体化的社团，向多元化大型二次元社团转变。</p><p style=\"text-align: start;\">动漫含义</p><p style=\"text-align: start;\">动漫是动画与漫画的集合，动漫与动画的关系是包含与被包含关系，并非同属关系。</p><p style=\"text-align: start;\">所有的动画、漫画作品都可以称之为<a href=\"https://zhidao.baidu.com/search?word=%B6%AF%C2%FE%D7%F7%C6%B7&amp;fr=iknow_pc_qb_highlight\" target=\"\">动漫作品</a>，但不可说动画或者漫画是动漫，原因是在语义上：动画和漫画是两种不同的艺术形式的作品，而并无“动漫”这种艺术形式的作品，它只是一个由<a href=\"https://zhidao.baidu.com/search?word=%D6%D0%B9%FA%B1%BE%CD%C1&amp;fr=iknow_pc_qb_highlight\" target=\"\">中国本土</a>所创的合成词。在很多场合，“动漫”一词被误用于指“动画”，十分容易引发歧义。</p><p style=\"text-align: start;\"><br></p>', '2023-05-05 08:00:00', '1');
INSERT INTO `team` VALUES (7, 2, '魔术协会', 'http://localhost:8089/images/f091d25b-6e18-4d47-a202-c98b407c6b0e.png', '<p style=\"text-align: start;\">魔术是以不断变化让人捉摸不透并带给观众惊奇体验为核心的一种表演艺术，是制造奇妙的艺术。如果你也想像魔术师一样技法变幻无穷，那么就加入欧亚学院魔术协会吧！</p><p style=\"text-align: start;\">魔术协会成立于2014年，是一个集学校社团活动、大型商业演出、道具研发、魔术教学为一体的综合性社团，2017年正式加入陕西省高校魔术联盟并任职理事职务。 2018、2019年连续两年被评为西安欧亚学院“五星级社团”，其中2019年综合评分位列全校各社团第一，学校迎新晚会等各大活动都会出现魔术协会的身影。</p><p style=\"text-align: start;\"><img src=\"https://pic1.zhimg.com/80/v2-e9a418370d7cab948800ce909cb35428_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">大多数人对于魔术都是零基础，而魔术协会就是为了培养更多大学生魔术师，尽可能让每一位在魔术协会学习过的同学都具备承接商业演出的能力，为大学生提供社会实践与就业的机会。</p><p style=\"text-align: start;\"><img src=\"https://pic3.zhimg.com/80/v2-76102d88bd31ae4afb3fd9e1e10d981a_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">魔术协会不仅在校内是知名社团，在业界也是非同凡响。协会每年都会参加各种公益活动以及商业演出，并多次参加陕西省电视台举办的亲子活动，也曾多次赴外省宣传和演出，国际魔术大师也与魔术协会有合作交流。2020年初，前任魔术协会会长邢嘉良、蒋艺嘉、朱城昊等人，在石家庄创办了星宇魔幻艺术培训学校，让更多人可以接触并了解魔术。</p><p style=\"text-align: start;\"><img src=\"https://pic4.zhimg.com/80/v2-f55342e8a6ffba1a50789e5fe9bbf873_720w.webp\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-align: start;\">魔术源于生活，奇迹只因你我！魔术协会期待你的到来。</p>', '2023-05-05 08:00:00', '1');
INSERT INTO `team` VALUES (8, 3, '乒乓球协会', 'http://localhost:8089/images/bfc96756-fb96-41e4-a260-ba90fcc16c08.png', '<p style=\"text-indent: 30px; text-align: justify;\"> 乒协团支部，是一个朝气蓬勃，充满生机和活力，拥有优良的作风，积极向上，在各方面积极都要求进步的团组织。在老师的领导下，该支部一步一个脚印，共同努力，共同成长，共同进步，无论是乒乓球技术，支部学风建设，还是校园活动方面，都取得了优良的成绩。以下是该团支部的事迹材料。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/B/F3/4F/30EC3131D9E977CBA7DBB8D7C3E_27738156_15361.png\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">（一）部门介绍</p><p style=\"text-indent: 30px; text-align: start;\">1.技术部：专攻技术要求有一定乒乓球基础，其中表现优异者将会代表乒协或者学校参加各类比赛活动，并且向学校校队输送优秀乒乓球运动员为校际比赛做好基础工作。</p><p style=\"text-indent: 30px; text-align: start;\">2.宣传部：该部门在提升乒乓球技术的同时主要负责实体宣传（如：板报，海报、喷绘，毛笔字横幅等）以及各类乒乓球比赛或由乒乓球爱好者协会负责举办的其他类活动的拍照摄影，另需协助媒体部进行微信平台的文稿编撰工作。</p><p style=\"text-indent: 30px; text-align: start;\">3.媒体部：该部门在提升乒乓球技术的同时将会定期进行计算机各类应用（PS,会声会影，微信平台等）的培训，主要负责乒乓球爱好者协会微信平台各类消息，通知以及乒乓球教程，视频的发布；在乒协举办的各类活动中调试媒体设备，以及在社团活动开始前制作相应的宣传喷绘、视频等。</p><p style=\"text-indent: 30px; text-align: start;\">4.裁判部：该部门在提升乒乓球技术得同时日常会注重培养正确的比赛意识，定期进行裁判员的培训工作，再乒协或学校体育部举办的乒乓球比赛活动中负责裁判工作。</p><p style=\"text-indent: 30px; text-align: start;\">5.外联部：该部门在提升乒乓球技术得同时，积极外出与社会进行接触，一些商家进行协商合作来获得一定资源用于社团发展，是一个大型社团必不可少的组成部分，是社团最重要的经济来源。</p><p style=\"text-indent: 30px; text-align: start;\">6.女生部：该部门服务于我校爱好乒乓球的女生，专注于提升女性乒乓球技术水平，组建女乒乓球队参加各类乒乓球比赛，并负责活动前的准备工作。</p><p style=\"text-indent: 30px; text-align: start;\">7.秘书部：该部门主要负责乒协各项活动相关资料的汇总等事务性日常工作，对乒协人事档案进行管理，及时传达各项文件的通知，整理修改各项活动的通讯并及时报导。</p><p style=\"text-indent: 30px; text-align: start;\">8.文艺部：该部门主要负责乒协联谊才艺表演，活动现场氛围。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/C/52/31/78841FC656743E3B385DBBF92A5_576248AF_22111.jpg\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">(二)团结工作</p><p style=\"text-indent: 30px; text-align: justify;\"> &nbsp;我们相信:对任何一个集体而言，团结的重要性是不言而喻的。团结是一个集体生存的基础，是支部之所以成为整体的保障。所以，一年来，这个支部十分注重支部内的团结工作。支部和部门内部分工明确，责任到人，这有利于充分发挥个人长处，同时强调互相配合，积极推动通力合作。支部通过内部团结工作，坦诚交流意见和建议，发现了自身的缺点和不足，有效地促进了支部工作的展开。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/D/E0/6C/4B93DCB6BD2D481E775A0F16B7C_74002D1C_20FEF.jpg\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">(三)学习工作</p><p style=\"text-indent: 30px; text-align: justify;\"> &nbsp;一年来，团支部在保障其他工作得到发展的同时，一直不忘把更多的心思放在学习上，毕竟学习才是他们的首要任务。经过全体同学的共同努力，该支部在学习方面取得了很大的进步，同学们经过在一起探讨学习上的问题，互帮互助，共同进步。在今后的学习中，该支部同学仍将保持并发扬这种支部风学风，靠自己的努力在今后的日子里争取更大的成绩。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/E/D8/65/AF8EAAA70B230819A4ED99B7FBB_62FCC220_24529.jpg\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">（四）开展丰富多彩的课外活动</p><p style=\"text-indent: 30px; text-align: start;\"> &nbsp;在团支部带领下，我们社团活动丰富多彩，在学校团委和团支部组织下，课余时间，同学们打乒乓球训练，本院以及各个学院之间开展球赛，同学们充满着青春的活力，洋溢着青春的风采，享受着生命的快乐和幸福。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/0/80/AF/51DE5923514E28A018B1E4856F8_9E8D74E1_159D5.png\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">（五）分工明确，力创佳绩</p><p style=\"text-indent: 30px; text-align: justify;\"> &nbsp;乒协是一个团结的社团，大家能够尽地各尽其职，发挥各方面的优势，力致将团部的工作引入正轨。事实也正是如此，由于确的分工，支部很快便开创了自己的特色，结社团中的团员，搞了一次又一次精彩丰富的比赛，赢得了大家一致的好评。团员们在一次组织生活中不仅有来自此次主旨生活本的收获，而且巩固、提高了团员对团本身的识。他们的工作准则，不仅丰富了大家的知识，而且增强了班中的凝聚力。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/6/97/03/AE92BEB718EA3812D9CE501C5C4_FF085F8A_1506B.png\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: start;\">（六）改革创新</p><p style=\"text-indent: 30px; text-align: justify;\"> &nbsp;社团活动多种多样，为了让社团活动更加有活力，我们社团新增文艺部。文艺部肩负着营造大队同学艺术文化氛围的重任，我们通过开展一系列丰富多彩的文艺活动，将欢声笑语注入点线之间的大学生活。</p><p style=\"text-indent: 30px; text-align: center;\"><img src=\"https://xtw.hebiace.edu.cn/__local/0/A0/1C/A2FE9A0E9A0E2A4B0EA8E4EB113_D5F7A31E_DE72.png\" alt=\"\" data-href=\"\" style=\"height: auto;\"></p><p style=\"text-indent: 30px; text-align: justify;\"> &nbsp;在本支部同学和团员们的共同努力下，我们创下了不少成果，总之成绩只代表过去，“路漫漫其修远兮，吾将上下而求索。\"在以后的岁月里，本团支部和支部委成员将带领全体团员不断探索，不懈追求和创新，将更加团结一致、锐意进取，齐心协力，共同进步。坚信在赋予新的活力和经过更大的努力之后乒协团支部将再创辉煌!</p>', '2023-05-05 08:00:00', '1');
INSERT INTO `team` VALUES (9, 3, '舞蹈团', 'http://localhost:8089/images/9c6628f4-df7d-4287-aad6-3e0a188c59ea.png', '<p><span style=\"color: rgb(51, 51, 51); background-color: rgb(255, 255, 255);\">如果你本身热爱舞蹈，那么读了大学后还是可以继续进行这样的兴趣的，舞蹈可以让我们的身心都获得健康发展。舞蹈团还经常可以去参演，生活很是丰富多彩哟。</span></p>', '2023-05-05 23:23:56', '1');
INSERT INTO `team` VALUES (10, 2, '诗社', 'http://localhost:8089/images/07230302-67cb-46ed-ac22-785779b8342f.png', '<p style=\"text-align: start;\">“人生万事须自为，跬步江山即寥廓……”24日，朗朗读诗声从海淀区定慧里小学操场传来。定慧里小学与首都师范大学共同成立诗社，两校将携手开展“大小”一体化诗词研学。</p><p style=\"text-align: start;\">新成立的诗社有个好听的名字——“慧心诗社”，寓意诗韵启智，慧心成长，其成立背后还有一段故事。慧心诗社的原形来自定慧里小学五年级6班学生田易辰最初创设的“清雅诗社”。田易辰通过发布诗歌创作主题、诗歌征集令等方式，号召身边的同学一起原创诗歌，汇总成《清雅诗集》。诗社成立至今开展了端午云诗会、清明诗会、读书分享会等多项活动。</p><p style=\"text-align: start;\"><img src=\"https://inews.gtimg.com/om_bt/OmVILNM8-nnIf8H5KCEyCG765O9wslYYDUL059WqYY19AAA/1000\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-align: start;\">“我们非常鼓励这种学生自发创设、且符合学校育人核心目标的活动和社团。”定慧里小学校长高艳说，为扩大辐射带动效应，该校决定在校级层面成立慧心诗社，为学生营造“爱读书，善读书，读好书”的校园阅读氛围。</p><p style=\"text-align: start;\">未来，首师大初等教育学院的大学生们将走进小学校园，与小学生们一起赏析传统诗词，创作诗歌，分享感悟。“这些孩子们毕业后都将进入小学当老师，与小学生一起参与诗社活动给他们提供了实习实践的锻炼机会。”首师大初等教育学院书记刘海涛表示，首都师范大学和定慧里小学在地理位置上算“邻居”。该院还计划利用地缘优势，邀请小学生走进大学校园，通过慧心诗社搭建青少年思想交融、共享的平台，开展形式更丰富的诗词研学活动。</p>', '2023-05-05 08:00:00', '1');
INSERT INTO `team` VALUES (11, 2, '五四青年社', 'http://localhost:8089/images/fa6a4a28-5888-491e-8a5a-1f1eae2d7058.png', '<p>既无岁月可回头，且敬青春一杯酒</p><p style=\"text-align: start;\">沧海桑田也好，岁月如梭也罢</p><p style=\"text-align: start;\">时间的长河都无法带走年轻的灵魂</p><p style=\"text-align: start;\">青春，不在年华，全在心境</p><p>只要心不老去，青春永无止境！</p><p style=\"text-align: start;\">“人生万事须自为，跬步江山即寥廓……”24日，朗朗读诗声从海淀区定慧里小学操场传来。定慧里小学与首都师范大学共同成立诗社，两校将携手开展“大小”一体化诗词研学。</p><p style=\"text-align: start;\">新成立的诗社有个好听的名字——“慧心诗社”，寓意诗韵启智，慧心成长，其成立背后还有一段故事。慧心诗社的原形来自定慧里小学五年级6班学生田易辰最初创设的“清雅诗社”。田易辰通过发布诗歌创作主题、诗歌征集令等方式，号召身边的同学一起原创诗歌，汇总成《清雅诗集》。诗社成立至今开展了端午云诗会、清明诗会、读书分享会等多项活动。</p><p style=\"text-align: start;\"><img src=\"https://inews.gtimg.com/om_bt/OmVILNM8-nnIf8H5KCEyCG765O9wslYYDUL059WqYY19AAA/1000\" alt=\"\" data-href=\"\" style=\"\"></p><p style=\"text-align: start;\">“我们非常鼓励这种学生自发创设、且符合学校育人核心目标的活动和社团。”定慧里小学校长高艳说，为扩大辐射带动效应，该校决定在校级层面成立慧心诗社，为学生营造“爱读书，善读书，读好书”的校园阅读氛围。</p><p style=\"text-align: start;\">未来，首师大初等教育学院的大学生们将走进小学校园，与小学生们一起赏析传统诗词，创作诗歌，分享感悟。“这些孩子们毕业后都将进入小学当老师，与小学生一起参与诗社活动给他们提供了实习实践的锻炼机会。”首师大初等教育学院书记刘海涛表示，首都师范大学和定慧里小学在地理位置上算“邻居”。该院还计划利用地缘优势，邀请小学生走进大学校园，通过慧心诗社搭建青少年思想交融、共享的平台，开展形式更丰富的诗词研学活动。</p>', '2023-05-05 08:00:00', '1');

